import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import SendIcon from '@mui/icons-material/Send';
import Button from '@mui/material/Button';
import { List, ListItem, ListItemText, Typography } from '@mui/material';
import ListItemIcon from '@mui/material/ListItemIcon'
import HistoryIcon from '@mui/icons-material/History';


function QueryComponent({ onQuerySubmit, queryHistory , fileName}) {
  const [query, setQuery] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('str_query', query);
    formData.append('filename', fileName);

    const response = await fetch('http://127.0.0.1:8000/query/', {
      method: 'POST',
      body: formData,
    });

    if (response.ok) {
      const data = await response.json();
      onQuerySubmit(data.query);
      setQuery('');
    } else {
      console.error('Query submission failed');
    }
  };

  return (
    <div className="query-container">
      <form onSubmit={handleSubmit}>
        <TextField
          id="standard-basic"
          label="Write a new Query"
          variant="standard"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <Button style={{ marginTop: '1%' }} variant="contained" onClick={handleSubmit} type="submit">
        Execute
      </Button>
      </form>
      <HistoryIcon fontSize="medium" />
      <div className="query-history">
  <Typography variant="h6" gutterBottom>
    Query History
  </Typography>
  <List>
  {[...queryHistory].reverse().map((query, index) => (
      <ListItem key={index} className={query === 'ERROR' ? 'error' : 'success'}>
        <ListItemIcon>
          <SendIcon fontSize="small" />
        </ListItemIcon>
        <ListItemText
          primary={
            <Typography variant="body2">
              {query}
            </Typography>
          }
        />
      </ListItem>
    ))}
  </List>
</div>
    </div>
  );
}

export default QueryComponent;
