import React, { useState } from 'react';
import FileContent from '../components/FileContent';

function DisplayPage() {
    const [query, setQuery] = useState('');
    const [fileContent, setFileContent] = useState('');

    // Dummy function to simulate fetching file content based on query
    const handleSubmitQuery = () => {
        // Simulating fetching file content based on query
        setFileContent(`File content for query "${query}"`);
    };

    return (
        <div>
            <h2>Display Page</h2>
            <div>
                <input
                    type="text"
                    placeholder="Enter your query..."
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <button onClick={handleSubmitQuery}>Submit Query</button>
            </div>
            <div>
                <FileContent content={fileContent} />
            </div>
        </div>
    );
}

export default DisplayPage;
