import React, { useState, useRef } from "react";
import UploadComponent from "./components/UploadComponent";
import FileDisplay from "./components/FileDisplay";
import QueryComponent from "./components/QueryComponent";
import "./App.css";

function App() {
  const [fileData, setFileData] = useState(null);
  const [queryHistory, setQueryHistory] = useState([]);
  const fileInputRef = useRef(null);

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (file && file.name.endsWith(".tex")) {
      const formData = new FormData();
      formData.append("file", file);

      const response = await fetch("http://127.0.0.1:8000/uploadfile/", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        setFileData({
          path: data.processed_file_path,
          name: data.original_filename,
        });
      } else {
        console.error("File upload failed");
      }
    } else {
      alert("Only .tex files are allowed");
    }
  };

  const handleDownloadClick = async () => {
    if (fileData && fileData.path) {
      try {
        const response = await fetch(`http://127.0.0.1:8000/downloadfile/${fileData.name}`, {
          method: 'GET',
          responseType: 'blob', // Ensure response type is blob
        });

        if (response.ok) {
          const blob = await response.blob();
          const url = window.URL.createObjectURL(new Blob([blob]));
          const link = document.createElement('a');
          link.href = url;
          link.setAttribute('download', fileData.name);
          document.body.appendChild(link);
          link.click();
          link.parentNode.removeChild(link);
        } else {
          console.error('Download request failed');
        }
      } catch (error) {
        console.error('Error downloading file:', error);
      }
    } else {
      alert('No file to download');
    }
  };


  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  const handleFileUpload = (data) => {
    setFileData({
      path: data.processed_file_path,
      name: data.original_filename,
    });
  };

  const handleQuerySubmit = (query) => {
    setQueryHistory([...queryHistory, query]);
  };

  return (
    <div className="app-container">
      <div className="navbar">
        <button className="navbar-button" onClick={handleUploadClick}>
          Upload
        </button>
        <h1 className="navbar-title">CV Tool</h1>
        <button className="navbar-button" onClick={handleDownloadClick}>Download</button>
      </div>
      <input
        type="file"
        accept=".tex"
        ref={fileInputRef}
        style={{ display: "none" }}
        onChange={handleFileChange}
      />
      <div className="content">
        <div className="left-pane">
          <QueryComponent
            onQuerySubmit={handleQuerySubmit}
            queryHistory={queryHistory}
          />
        </div>
        <div className="right-pane">
          {!fileData ? (
            <UploadComponent onFileUpload={handleFileUpload} />
          ) : (
            <FileDisplay fileData={fileData} fileName={fileData} />
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
