import React, { useEffect, useState } from 'react';
import Prism from 'prismjs';
import 'prismjs/components/prism-latex';
import 'prismjs/themes/prism.css';
import './FileDisplay.css'; // Ensure this file contains your custom CSS overrides

function FileDisplay({ fileData, fileName }) {
  const [fileContent, setFileContent] = useState('');

  useEffect(() => {
    const fetchFileContent = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:8000/getfile/?filename=${encodeURIComponent(fileName)}`);
        if (response.ok) {
          const text = await response.text();
          setFileContent(text);
        } else {
          console.error('Failed to fetch file content');
        }
      } catch (error) {
        console.error('Error fetching file:', error);
      }
    };

    fetchFileContent();
  }, [fileName]);

  useEffect(() => {
    if (fileContent) {
      Prism.highlightAll();
    }
  }, [fileContent]);

  return (
    <div className="file-display">
      <pre>
        <code className="language-latex">
          {fileContent}
        </code>
      </pre>
    </div>
  );
}

export default FileDisplay;
