import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ToolPage.css';

const ToolPage = () => {
  const [query, setQuery] = useState('');
  const [fileContent, setFileContent] = useState('');
  const [fileUploaded, setFileUploaded] = useState(false);

  useEffect(() => {
    // Fetch file content on component mount
    fetchFileContent();
  }, []);

  const fetchFileContent = () => {
    axios.get('/getfile/')
      .then(response => {
        if (response.data.message) {
          setFileUploaded(false);
          setFileContent('');
        } else {
          setFileContent(response.data.original_content);
          setFileUploaded(true);
        }
      })
      .catch(error => console.error('Error fetching file:', error));
  };

  const handleQueryChange = (event) => {
    setQuery(event.target.value);
    // Implement query handling if needed
  };

  const handleUploadFile = (event) => {
    const formData = new FormData();
    formData.append('file', event.target.files[0]);

    axios.post('/uploadfile/', formData)
      .then(response => {
        // Handle successful file upload
        console.log('File uploaded successfully:', response.data);
        fetchFileContent(); // Refresh file content after upload
      })
      .catch(error => console.error('Error uploading file:', error));
  };

  const handleDownloadFile = () => {
    // Trigger file download
    window.open('/downloadfile/');
  };

  return (
    <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px' }}>
      <h1>File Tool</h1>
      <div style={{ display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <input
            type="text"
            value={query}
            onChange={handleQueryChange}
            placeholder="Enter your query..."
            style={{ width: '100%', padding: '8px' }}
          />
          {/* Additional query handling components can go here */}
        </div>
        <div style={{ flex: 1, marginLeft: '20px' }}>
          {fileUploaded ? (
            <div>
              <h2>File Content:</h2>
              <pre>{fileContent}</pre>
              <button onClick={handleDownloadFile}>Download File</button>
            </div>
          ) : (
            <div>
              <h2>No file uploaded yet</h2>
              <input type="file" onChange={handleUploadFile} />
              {/* Implement upload button */}
            </div>
          )}
        </div>
      </div>
      <div style={{ marginTop: '20px' }}>
        <button onClick={() => window.location.reload()}>Upload New File</button>
      </div>
    </div>
  );
}

export default ToolPage;
