import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import SendIcon from '@mui/icons-material/Send';
import Button from '@mui/material/Button';
import { List, ListItem, ListItemText, Typography } from '@mui/material';
import ListItemIcon from '@mui/material/ListItemIcon';


function QueryComponent({ onQuerySubmit, queryHistory }) {
  const [query, setQuery] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('str_query', query);

    const response = await fetch('http://127.0.0.1:8000/query/', {
      method: 'POST',
      body: formData,
    });

    if (response.ok) {
      const data = await response.json();
      onQuerySubmit(data.query);
      setQuery('');
    } else {
      console.error('Query submission failed');
    }
  };

  return (
    <div className="query-container">
      <form onSubmit={handleSubmit}>
        <TextField
          id="standard-basic"
          label="Make a Query"
          variant="standard"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <Button style={{ marginTop: '1%' }} variant="contained" endIcon={<SendIcon />} onClick={handleSubmit} type="submit">
        Send
      </Button>
      </form>
      <div className="query-history">
  <Typography variant="h6" gutterBottom>
    Query History
  </Typography>
  <List>
    {queryHistory.map((query, index) => (
      <ListItem key={index}>
        <ListItemIcon>
                    <FolderIcon />
                  </ListItemIcon>
        <ListItemText primary={query} />
      </ListItem>
    ))}
  </List>
</div>
    </div>
  );
}

export default QueryComponent;
