import logo from './logo.svg';
import './App.css';
import React, { useState } from "react";
import axios from "axios";


function App() {
  const [file, setFile] = useState(null);
  const [fileContent, setFileContent] = useState("");
  const [processedFilePath, setProcessedFilePath] = useState("");

  const handleFileChange = (event) => {
    const fileInput = event.target;
    const fileName = fileInput.files.length > 0 ? fileInput.files[0].name : 'No file chosen';
    setFileName(fileName);
};

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:8000/uploadfile/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setFileContent(response.data.original_content);
      setProcessedFilePath(response.data.processed_file_path);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const handleDownload = () => {
    window.location.href = `http://127.0.0.1:8000/downloadfile/?file_path=${processedFilePath}`;
  };

  return (
    <div>
      <label class="file-label" for="fileInput">Select File</label>
        <input id="fileInput" class="file-input" type="file" onChange={handleFileChange}/>
      {fileContent && (
        <div>
          <h3>Original File Content:</h3>
          <pre>{fileContent}</pre>
        </div>
      )}
      {processedFilePath && (
        <button onClick={handleDownload}>Download Processed File</button>
      )}
    </div>
  );
}

export default App;
