import React, { useState } from 'react';
import axios from 'axios';
import './QueryBox.css';

function QueryBox() {
    const [textInput, setTextInput] = useState(""); // State for input box text
    const [response, setResponse] = useState(null); // State for API response
    const [error, setError] = useState(null); // State for errors

    const handleTextInputChange = (event) => {
        setTextInput(event.target.value);
    };

    const handleSubmit = async () => {
        try {
            setError(null); // Reset error state
            const formData = new FormData();
            formData.append('str_query', textInput);

            const res = await axios.post('http://localhost:8000/query/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            setResponse(res.data); // Update the response state with the API response
        } catch (error) {
            setError(error.response?.data?.detail || error.message);
            setResponse(null); // Reset response state
        }
    };

    return (
        <div>
            <input
                type="text"
                placeholder="Enter text here"
                value={textInput}
                onChange={handleTextInputChange}
                className="input-text"
            />
            <button onClick={handleSubmit}>Submit</button>
            {response && <div className="response">Response: {JSON.stringify(response)}</div>}
            {error && <div className="error">Error: {error}</div>}
        </div>
    );
}

export default QueryBox;
