import React, { useState } from 'react';

function QueryComponent({ onQuerySubmit, queryHistory }) {
  const [query, setQuery] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('str_query', query);

    const response = await fetch('http://127.0.0.1:/query/', {
      method: 'POST',
      body: formData,
    });

    if (response.ok) {
      const data = await response.json();
      onQuerySubmit(data.query);
      setQuery('');
    } else {
      console.error('Query submission failed');
    }
  };

  return (
    <div className="query-container">
      <form onSubmit={handleSubmit}>
        <label>Make a Query</label>
        <input 
          type="text" 
          value={query} 
          onChange={(e) => setQuery(e.target.value)} 
        />
        <button onClick={handleSubmit} type="submit">Submit</button>
      </form>
      <div className="query-history">
        <h3>Query History</h3>
        <ul>
          {queryHistory.map((query, index) => (
            <li key={index}>{query}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default QueryComponent;
