import React, { useState } from 'react';
import FileUpload from '../components/FileUpload';
import FileContent from '../components/FileContent';
import './ToolPage.css';

function ToolPage() {
  const [fileContent, setFileContent] = useState("");
  const [showUpload, setShowUpload] = useState(true); // State to manage showing upload or content

  const handleFileContent = (content) => {
    setFileContent(content);
    setShowUpload(false); // Once file content is set, show content
  };

  const handleNewUpload = () => {
    setFileContent(""); // Clear file content
    setShowUpload(true); // Show upload component again
  };

  return (
    <div className="container">
      <div className="left">
        <input type="text" placeholder="Enter text here" />
        {showUpload ? (
          <FileUpload onFileUpload={handleFileContent} />
        ) : (
          <div>
            <button onClick={handleNewUpload}>Upload New File</button>
            <FileContent content={fileContent} />
          </div>
        )}
      </div>
      <div className="right">
        {/* This side remains unchanged */}
        <FileContent content={fileContent} />
      </div>
    </div>
  );
}

export default ToolPage;
