import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import SendIcon from '@mui/icons-material/Send';
import Button from '@mui/material/Button';
import { List, ListItem, ListItemText, Typography } from '@mui/material';
import ListItemIcon from '@mui/material/ListItemIcon';
import HistoryIcon from '@mui/icons-material/History';

function QueryComponent({ onQuerySubmit, queryHistory, fileName }) {
  const [query, setQuery] = useState('');
  const [history, setHistory] = useState(queryHistory); // State for query history
  const [loading, setLoading] = useState(false); // State for loading indicator

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true); // Start loading indicator

    const formData = new FormData();
    formData.append('str_query', query);
    formData.append('filename', fileName);

    try {
      const response = await fetch('http://127.0.0.1:8000/query/', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Query submission failed');
      }

      const data = await response.json();
      const options = {
        hour: 'numeric',
        minute: 'numeric',
        second: 'numeric'
      };
      const timestamp = new Date().toLocaleTimeString([], options);
      const newQuery = { status: data.status, query: data.query, timestamp: timestamp };

      onQuerySubmit(data.query);
      setHistory([...history, newQuery]);
      setQuery('');
    } catch (error) {
      console.error('Error submitting query:', error);
      // Display an error message to the user
      // You can use a Snackbar or another UI component for this
    } finally {
      setLoading(false); // Stop loading indicator
    }
  };

  return (
    <div className="query-container">
      <form onSubmit={handleSubmit}>
        <TextField
          id="standard-basic"
          label="Write a new Query"
          variant="standard"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <Button style={{ marginTop: '1%' }} variant="contained" onClick={handleSubmit} type="submit">
          Execute
        </Button>
      </form>
      <HistoryIcon fontSize="medium" />
      <div className="query-history">
        <Typography variant="h6" gutterBottom>
          Query History
        </Typography>
        <List>
          {[...history].reverse().map((item, index) => (
            <ListItem key={index} className={item.status === 'ERROR' ? 'error' : 'success'}>
              <ListItemIcon>
                <SendIcon fontSize="small" />
              </ListItemIcon>
              <ListItemText
                primary={
                  <Typography variant="body2">
                    {item.query} - {item.timestamp}
                  </Typography>
                }
              />
            </ListItem>
          ))}
        </List>
      </div>
      {loading && <p>Loading...</p>} {/* Display loading indicator */}
    </div>
  );
}

export default QueryComponent;
