import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ToolPage = () => {
  const [query, setQuery] = useState('');
  const [fileContent, setFileContent] = useState('');
  const [fileUploaded, setFileUploaded] = useState(false);

  useEffect(() => {
    // Fetch file content on component mount
    fetchFileContent();
  }, []);

  const fetchFileContent = () => {
    axios.get('http://localhost:8000/getfile/')
      .then(response => {
        if (response.data.message) {
          setFileUploaded(false);
          setFileContent('');
        } else {
          setFileContent(response.data.original_content);
          setFileUploaded(true);
        }
      })
      .catch(error => console.error('Error fetching file:', error));
  };

  const handleQueryChange = (event) => {
    setQuery(event.target.value);
    // Implement query handling if needed
  };

  const handleUploadFile = (event) => {
    const formData = new FormData();
    formData.append('file', event.target.files[0]);

    axios.post('http://localhost:8000/uploadfile/', formData)
      .then(response => {
        // Handle successful file upload
        console.log('File uploaded successfully:', response.data);
        fetchFileContent(); // Refresh file content after upload
      })
      .catch(error => console.error('Error uploading file:', error));
  };

  const handleDownloadFile = () => {
    // Trigger file download
    window.open('http://localhost:8000/downloadfile/');
  };

  const handleUploadNewFile = () => {
    // Reload the page to upload a new file
    window.location.reload();
  };

  return (
    <div className="container">
      <h1>File Tool</h1>
      <div className="flex-container">
        <div className="flex-item">
          <input
            type="text"
            value={query}
            onChange={handleQueryChange}
            placeholder="Enter your query..."
            className="input-text"
          />
          {/* Additional query handling components can go here */}
        </div>
        <div className="flex-item upload-section">
          {fileUploaded ? (
            <div className="file-content">
              <h2>File Content:</h2>
              <pre>{fileContent}</pre>
              <button className="button" onClick={handleDownloadFile}>Download File</button>
            </div>
          ) : (
            <div>
              <h2>No file uploaded yet</h2>
              <input type="file" onChange={handleUploadFile} className="file-upload" />
              {/* Implement upload button */}
            </div>
          )}
        </div>
      </div>
      <div>
        <button className="button button-secondary" onClick={handleUploadNewFile}>Upload New File</button>
      </div>
    </div>
  );
}

export default ToolPage;
