import React, { useState } from 'react';
import axios from 'axios';

import './QueryBox.css';

function QueryBox() {
    const [textInput, setTextInput] = useState(""); // State for input box text
    const [response, setResponse] = useState(null); // State for API response
    const [error, setError] = useState(null); // State for errors

    const handleTextInputChange = (event) => {
        setTextInput(event.target.value);
    };

    const handleSubmit = async () => {
        try {
            setError(null); // Reset error state
            const res = await axios.post('http://localhost:8000/uploadfile/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
                body: { str_query: textInput },
            });

            if (!res.ok) {
                throw new Error(`Error: ${res.statusText}`);
            }

            const data = await res.json();
            setResponse(data); // Update the response state with the API response
        } catch (error) {
            setError(error.message);
            setResponse(null); // Reset response state
        }
    };

    return (
        <div className="left-side">
            <input
                type="text"
                placeholder="Enter text here"
                value={textInput}
                onChange={handleTextInputChange}
                className="input-text"
            />
            <button onClick={handleSubmit}>Submit</button>
            {response && <div className="response">Response: {JSON.stringify(response)}</div>}
            {error && <div className="error">Error: {error}</div>}
        </div>
    );
}

export default QueryBox;
