import React, { useState } from 'react';
import FileUpload from '../components/FileUpload';
import FileContent from '../components/FileContent';
import QueryBox from '../components/QueryBox';
import './ToolPage.css';

function ToolPage() {
  const [fileContent, setFileContent] = useState(null); // Using null to indicate no file uploaded

  const handleFileContent = (content) => {
    setFileContent(content);
  };

  const handleNewUpload = () => {
    setFileContent(null); // Reset fileContent to null to show FileUpload component again
  };


  return (
    <div className="toolpage-container">
      <QueryBox />
      <div className="right-side">
        {fileContent ? (
          <div className="file-content">
            <button className="upload-new-button" onClick={handleNewUpload}>Upload New File</button>
            <FileContent content={fileContent} />
          </div>
        ) : (
          <FileUpload onFileUpload={handleFileContent} />
        )}
      </div>
    </div>
  );
}

export default ToolPage;
