import logo from './logo.svg';
import './App.css';
import React, { useState } from "react";
import axios from "axios";

/*
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}
*/

function App() {
  const [file, setFile] = useState(null);
  const [processedFilePath, setProcessedFilePath] = useState("");

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:8000/uploadfile", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setProcessedFilePath(response.data.processed_file_path);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const handleDownload = () => {
    window.location.href = `http://localhost:8000/downloadfile?file_path=${processedFilePath}`;
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload and Process File</button>
      {processedFilePath && (
        <button onClick={handleDownload}>Download Processed File</button>
      )}
    </div>
  );
}

export default App;
