import React from 'react';
import { Typography, Link, makeStyles } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  footer: {
    backgroundColor: '#333',
    color: '#fff',
    padding: theme.spacing(3),
    textAlign: 'center',
    position: 'fixed',
    bottom: 0,
    width: '100%',
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    '&:hover': {
      textDecoration: 'underline',
    },
  },
}));

const Footer = () => {
  const classes = useStyles();

  return (
